let article = "";

// Introduction
article += $('Blog Planner').first().json.output.introduction;
article += "\n\n";

for (const item of $input.all()) {
  // Add the chapter title as a Markdown level 2 header
  article += "## " + item.json.chapter_title + "\n\n";

  // Insert the chapter image if it exists (using Markdown image syntax)
  if (item.json.source_url) {
    article += `![${item.json.title.rendered}](${item.json.description.rendered})\n\n`;
  }

  // Add the chapter content
  article += item.json.chapter_content;
  article += "\n\n";
}

// Conclusion
article += "## Kết luận\n\n";
article += $('Blog Planner').first().json.output.conclusion;
article += "\n\n";

// Call-to-Action (CTA)
article += $('Blog Planner').first().json.output.cta + "\n\n";
article += "Có thể bạn quan tâm: [" + $('Blog Planner').first().json.output.cta_url + "](" + $('Blog Planner').first().json.output.cta_url + ")\n\n";

// Company Offering
article += "## Về Tín Thành Phát\n\n";
article += $('Blog Planner').first().json.output.company_offering;

return [
  {
    "article": article
  }
];

