// Nhận dữ liệu đầu vào từ n8n
const inputDateTime = items[0].json.dateTime; // Giả sử đầu vào có key "dateTime"

function convertToISOFormat(dateTimeStr) {
    let [datePart, timePart] = dateTimeStr.split(' ');
    let [day, month, year] = datePart.split('-');
    return `${year}-${month}-${day}T${timePart}`;
}

// Chuyển đổi định dạng
const outputDateTime = convertToISOFormat(inputDateTime);

return [{ json: { formattedDateTime: outputDateTime } }];
