// Hàm loại bỏ ký tự không mong muốn và kiểm tra JSON hợp lệ
function cleanJsonString(input) {
  if (typeof input !== "string") {
    throw new Error("Invalid input type, expected a string");
  }

  // Loại bỏ ký hiệu mở đầu và kết thúc JSON
  let cleanedString = input.replace(/^```json\n/, "").replace(/\n```$/, "");

  // Loại bỏ ký tự điều khiển không mong muốn
  cleanedString = cleanedString.replace(/[\u0000-\u001F\u007F-\u009F]/g, "");

  return cleanedString;
}

// Hàm xử lý nội dung để lấy ra các prompts và phân loại nội dung
function processContent(content) {
  if (!Array.isArray(content)) {
    return { text: "No valid content available.", imagesPrompts: [] };
  }

  const imagesPrompts = [];
  let textContent = [];

  content.forEach((block, index) => {
    switch (block.type) {
      case "paragraph":
        textContent.push(block.content);
        break;
      case "heading":
        textContent.push(`${"#".repeat(block.level || 1)} ${block.content}`);
        break;
      case "image_prompt":
        imagesPrompts.push({
          image_id: `IMAGE_${index}`, // Tạo ID hình ảnh duy nhất
          prompt: block.prompt || null, // Lấy prompt
          alt_tag: block.alt_tag || null, // Lấy alt_tag nếu có
          img_slug: block.img_slug || null, // Lấy img_slug nếu có
        });
        textContent.push(`{{IMAGE_PLACEHOLDER_${index}}}`); // Thêm placeholder vào văn bản
        break;
      default:
        // Handle other types or ignore unrecognized types
        break;
    }
  });

  return { text: textContent.join("\n\n"), imagesPrompts };
}

// Vòng lặp xử lý từng mục nhập
for (const item of $input.all()) {
  try {
    const cleanedJson = cleanJsonString(item.json.output); // Làm sạch chuỗi JSON
    const parsedOutput = JSON.parse(cleanedJson); // Phân tích JSON

    const { text, imagesPrompts } = processContent(parsedOutput.content || []);

    // Cập nhật item.json với dữ liệu mới
    item.json.title = parsedOutput.title;
    item.json.slug = parsedOutput.slug;
    item.json.meta_description = parsedOutput.meta_description;
    item.json.keywords = parsedOutput.keywords;
    item.json.content = text;
    item.json.imagesPrompts = imagesPrompts;

  } catch (error) {
    item.json.error = `Error processing 'output': ${error.message}`;
  }
}

// Trả về danh sách đã xử lý
return $input.all();

