const data = items[0].json; // Access the first (and only) object in the array
const prompts = [];

// Iterate over all properties in the object
for (const key in data) {
  if (data.hasOwnProperty(key)) {
    prompts.push({ json: { prompt: data[key] } });
  }
}

return prompts;

